import '../../App.css';
import React, { useState, useEffect, useRef } from "react";

function Home() {

    const [loading, setLoading] = useState(true);
    const [neoData, setNeoData] = useState({});

    const myElements = useRef();

    let dateResults = [];

    // recent date (7 day)
    function getLastWeekDates() {
        for (let i = 0; i < 7; i++) {
            let date = new Date();
            date.setDate(date.getDate() - i);
            let formattedDate = date.toISOString().split('T')[0];
            dateResults.unshift(formattedDate);
        }
    }
    getLastWeekDates()

    let resData;
    function grabData() {
            fetch(`https://api.nasa.gov/neo/rest/v1/feed?start_date=${dateResults[0]}&end_date=${dateResults[6]}&api_key=nqtmVSdy5emfjwc4QfPtLcr5M4ijWvwqzqwMLmdr`)
            .then( async(res) => {
                resData =  await res.json();
                myElements.current = resData;
                for (let i = 0; i < 7; i++) {
                    console.log(myElements.current.near_earth_objects[dateResults[i]])
                }

                setLoading(false)

            }).catch( (err) => {
            console.log(err)
        })
    }


    useEffect(  () => {
        if (loading)
        {
            grabData()
        }
        else if (!loading)
        {
            setNeoData(myElements.current.near_earth_objects[dateResults[0]])
        }
    }, [loading])
    return (
        <div>
            {loading &&
                <div>
                    LOADING
                </div>
            }
            {!loading &&
                <div className="relative overflow-x-auto shadow-md sm:rounded-lg">
                    <table className="w-full text-sm text-left text-gray-500 dark:text-gray-400">
                        <thead className="text-xs text-gray-700 uppercase bg-gray-50 dark:bg"></thead>
                        <tr>
                            <th scope="col" className="px-6 py-3">

                            </th>
                            <th scope="col" className="px-6 py-3">

                            </th>
                            <th scope="col" className="px-6 py-3">

                            </th>
                            <th scope="col" className="px-6 py-3">

                            </th>
                        </tr>
                    </table>
                </div>
            }
        </div>
    )
}

export default Home;
