import '../../App.css';
import React, { useState, useEffect, useRef } from "react";

function Home() {
    const [loading, setLoading] = useState(true);
    const [neoData, setNeoData] = useState({});
    const [weeklyArray, setWeeklyArray] = useState([])

    // recent date (7 day)
    function getLastWeekDates() {
        for (let i = 0; i < 7; i++) {
            let date = new Date();
            date.setDate(date.getDate() - i);
            let formattedDate = date.toISOString().split('T')[0];
            const handleAdd = (formattedDate) => {
                const newDates = [...formattedDate]
                newDates.push(formattedDate)
                setWeeklyArray(newDates)
            }
        }
    }
    getLastWeekDates()

    let resData;

    // Note about fetch options:
    // no-cors mode won't magically make our CORS request work
    // One of the side effects of no-cors is that it tells frontend JS code from
    // seeing contents of the response body and headers under all circumstances

    // Browsers by default block frontend code from accessing resources cross-origin.
    // If Access-Control-Allow-Origin is in a response, then browsers relax that blocking allow
    // code to access the response


    let thisWeek = []
    function grabData() {
            fetch(`redacted/`)
            .then( async(res) => {
                resData =  await res.json();
                setNeoData(resData)
                setLoading(false)
            }).catch( (err) => {
            console.log(err)
        })
    }

    useEffect(  () => {
        if (loading)
        {
            grabData()
        }
        else if (!loading)
        {
            console.log(weeklyArray)

        }
    }, [loading, weeklyArray])


    return (
        <div>
            {loading &&
                <div>
                    LOADING
                </div>
            }
            {!loading &&
                <div className="flex relative w-1/2 h-full overflow-x-auto shadow-md sm:rounded-lg">
                    <table className="w-full text-sm text-left text-gray-500 dark:text-gray-400">
                        <thead className="text-xs text-gray-700 uppercase bg-gray-50 dark:bg-gray-700 dark:text-gray-400">
                            <tr>
                                {
                                    thisWeek.map( (item, id) => (
                                        <th key={id}>{item}</th>
                                    ))
                                }
                            </tr>
                        </thead>
                        <tbody>
                            <tr className="bg-white border-b dark:bg-gray-800 dark:border-gray-700">


                                {/*{*/}
                                {/*        neoData.map((item, ind) => (<th key={ind} scope="col" className="px-6 py-4"></th>))*/}
                                {/*    }*/}

                            </tr>
                        </tbody>
                    </table>
                </div>
            }
        </div>
    )
}

export default Home;
